import firebase from "firebase";
var firebaseConfig = {
  apiKey: "AIzaSyAJMkmkuBkjz8n7ednE0_KtRqw1A7_6azA",
  authDomain: "vexchangee.firebaseapp.com",
  projectId: "vexchangee",
  storageBucket: "vexchangee.appspot.com",
  messagingSenderId: "41629467344",
  appId: "1:41629467344:web:363d344148c88b7b5514d5",
  measurementId: "G-5M0EEER0RT",
};
// Initialize Firebase
firebase.initializeApp(firebaseConfig);
const messaging = firebase.messaging();
messaging.setBackgroundMessageHandler(function (payload) {
  const promiseChain = clients
    .matchAll({
      type: "window",
      includeUncontrolled: true,
    })
    .then((windowClients) => {
      for (let i = 0; i < windowClients.length; i++) {
        const windowClient = windowClients[i];
        windowClient.postMessage(payload);
      }
    })
    .then(() => {
      return registration.showNotification("my notification title");
    });
  return promiseChain;
});
self.addEventListener("notificationclick", function (event) {
  console.log(event);
});
export default { messaging };
