import React from "react";
import { Col, Grid, Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { getALL } from "redux/index";
import { CardNoFooter } from "../components/Card/Card";
import EnhancedTable from "../components/SuperTable/AdvanceTable";
import { getAdvanceData, getMaterialHeader } from "../redux/Selector/Selectors";
import * as variable from "../variables/Variables";
import { openPopup, closePopup, ShowPopUp } from "components/Modal/Modal";
import { UserCard } from "components/UserCard/UserCard.jsx";
import avatar from "assets/img/faces/face-3.jpg";

function Post() {
  let materialBody = useSelector((state) => getAdvanceData(state));
  let materialHeader = useSelector((state) => getMaterialHeader(state));
  let dispatch = useDispatch();
  let [visible, setVisible] = React.useState(false);
  React.useEffect(() => {
    const fetchPostList = async () => {
      try {
        const response = await dispatch(getALL());
        console.log("success: ", response);
      } catch (errpr) {
        console.log("Failed to fetch product list: ", errpr);
      }
    };
    fetchPostList();
  }, []);
  function openModal() {
    setVisible(openPopup);
  }
  function closeModal() {
    setVisible(closePopup);
  }
  return (
    <React.Fragment>
      <ShowPopUp
        visible={visible}
        //   popupContent={this.state.popupConten}
        onCLose={closeModal}
        length="80%"
      >
        <UserCard
          bgImage="https://ununsplash.imgix.net/photo-1431578500526-4d9613015464?fit=crop&fm=jpg&h=300&q=75&w=400"
          avatar={avatar}
          name="Mike Andrew"
          userName="michael24"
          description={
            <span>
              "Lamborghini Mercy
              <br />
              Your chick she so thirsty
              <br />
              I'm in that two seat Lambo"
            </span>
          }
        />
      </ShowPopUp>
      <div className="content">
        <Grid fluid style={{ margin: 0, padding: 0 }}>
          <CardNoFooter
            titlet="Account list"
            content={
              <Row>
                <Col xs={8}>
                  <EnhancedTable
                    headCells={materialHeader}
                    bodyData={materialBody}
                    notShowing={[variable.islock]}
                    actionbuttonlist={["read"]}
                  />
                  <Col xs={4}>
                    <button onClick={openModal}>Open it</button>
                  </Col>
                </Col>
              </Row>
            }
          />
        </Grid>
      </div>
    </React.Fragment>
  );
}

export default Post;
