import Modal from "react-awesome-modal";
import React, { Component } from "react";
import GreenSuccess from "assets/img/green tick.jpg";
import "./Popup.css";
/**
 * * Mở popup lên
 */
export function openPopup() {
  return true;
}
// export function openSuccess(){
//   return true
// }
/**
 * * Đóng popup lại
 */
export function closePopup() {
  return false;
}
// export function closeSuccess(){
//   return false
// }
export const ShowPopUp = (argument) => {
  const width = argument.length;
  return (
    <Modal
      visible={argument.visible}
      width={width}
      effect="fadeInDown"
      onClickAway={argument.onCLose}
    >
      {argument.children}
    </Modal>
  );
};
export const SuccessPopUp = (argument) => {
  const width = argument.length;
  return (
    <Modal
      visible={argument.visible}
      width={width}
      effect="fadeInDown"
      onClickAway={()=>setTimeout(function(){argument.onClose;},2000)}
    >
      <div style={{display:"flex",flexDirection:"column",justifyContent:"center",alignItems:"center"}}>
        <img src={GreenSuccess} width={100} height={100} />
        <p style={{ textAlign: "center", color: "green", fontWeight: "bold" }}>
          Thành công
        </p>
      </div>
    </Modal>
  );
};
