import { faGoogle } from "@fortawesome/free-brands-svg-icons";
import { faPhoneAlt, faUser } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Box } from "@material-ui/core";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import React from "react";
import "../components/SignIn/style.scss";
import { FormControll } from "components/Formik/FormikControl";
import { Formik } from "formik";
import * as variable from "variables/Variables";
import * as Yup from "yup";
import { useSelector } from "react-redux";
import {getSignInForm} from "redux/Selector/Selectors"


let width = 240;
let marginBottom = 5;
let headeFontSize = 20;
let choseSignInTypeHeight = 30;
let choseSignInTypeWidth = "80%";
let startIcon = 15;
let chooseSignInPadding = 20;
let attensionPadding = 10;
// * Style cho may cai nut chon loai dang nhap
let choseStyle = {
  height: choseSignInTypeHeight,
  padding: 0,
  marginBottom: 10,
  width: choseSignInTypeWidth,
  fontSize: 12,
};

const initialValue = {
  [variable.username]: "",
  [variable.password]: "",
};
const onSubmit = () => {};

const validationSchema = Yup.object({
  [variable.password]: Yup.string().required([variable.wrongpassword]),
});
function handleSubmit(){

}

function Signin() {
  const signInForm=useSelector(state=>getSignInForm(state))
  return (
    <div
      style={{ display: "flex", flexDirection: "row", alignItems: "center" }}
    >
      {/* <div className="login-page">
        <div className="form">
          <Button
            style={{
              height: choseSignInTypeHeight,
              padding: chooseSignInPadding,
              marginBottom: 10,
              width: choseSignInTypeWidth,
              fontSize: 12,
            }}
            type="submit"
            startIcon={
              <FontAwesomeIcon
                icon={faUser}
                width={startIcon}
                height={startIcon}
              />
            }
          >
            Tên và mật khẩu
          </Button>
          <Button
            type="submit"
            style={{
              height: choseSignInTypeHeight,
              padding: chooseSignInPadding,
              marginBottom: 10,
              width: choseSignInTypeWidth,
              fontSize: 12,
              backgroundColor: "#e82e1e",
            }}
            startIcon={
              <FontAwesomeIcon
                icon={faGoogle}
                width={startIcon}
                height={startIcon}
              />
            }
          >
            Google
          </Button>
          <Button
            type="submit"
            style={{
              height: choseSignInTypeHeight,
              padding: chooseSignInPadding,
              marginBottom: 10,
              width: choseSignInTypeWidth,
              fontSize: 12,
              backgroundColor: "#c57a09",
            }}
            startIcon={
              <FontAwesomeIcon
                icon={faPhoneAlt}
                width={startIcon}
                height={startIcon}
              />
            }
          >
            Số điện thoại
          </Button>
        </div>
      </div> */}
      {/* <div style={{ fontSize: 25 }}>- -</div> */}
      <div className="login-page">
        <div className="form">
          <form className="login-form" onSubmit={(e) => handleSubmit(e)}>
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                paddingLeft: 15,
                fontFamily: "Roboto",
              }}
            >
              <p style={{ color: "grey", fontSize: headeFontSize }}>
                Thay đổi mật khẩu
              </p>
            </div>
            <TextField
              style={{ width: width, marginBottom: marginBottom }}
              type="text"
              variant="standard"
              label="Email"
              name="email"
              // onChange={(event) => this.handleChange(event)}
            />
            <div
              className="attensin"
              style={{ textAlign: "left", paddingLeft: attensionPadding }}
            >
              <p style={{ fontSize: 12, fontWeight: "bold" }}>
                Lưu ý: Mail sẽ đến trong vòng 5 phút
              </p>
            </div>
            <button type="submit" className="button">
              <p>
                <a href="#resend">Gửi yêu cầu</a>
              </p>
            </button>{" "}
            <div
              style={{
                textAlign: "center",
                color: "#bb8322",
                paddingLeft: 10,
                margin: "5px 5px 15px 0",
              }}
            >
              <p id="resend">Gửi lại yêu cầu trong {chooseSignInPadding}s</p>
            </div>
          </form>
        </div>
      </div>

      <div className="login-page">
        <div className="form">
          <form className="login-form" onSubmit={(e) => handleSubmit(e)}>
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                paddingLeft: 15,
                fontFamily: "Roboto",
              }}
            >
              <p style={{ color: "grey", fontSize: headeFontSize }}>
                Mật khẩu mới
              </p>
            </div>
            <TextField
              size="medium"
              style={{ marginBottom: marginBottom }}
              type="text"
              variant="outlined"
              label="Email"
              name="email"
              // onChange={(event) => this.handleChange(event)}
            />
            <div
              className="attensin"
              style={{ textAlign: "left", paddingLeft: attensionPadding }}
            >
              <p style={{ fontSize: 12, fontWeight: "bold" }}>
                Lưu ý: Mail sẽ đến trong vòng 5 phút
              </p>
            </div>
            <button type="submit" className="button">
              <p>
                <a href="#resend">Gửi yêu cầu</a>
              </p>
            </button>{" "}
            <div
              style={{
                textAlign: "center",
                color: "#bb8322",
                paddingLeft: 10,
                margin: "5px 5px 15px 0",
              }}
            >
              <p id="resend">Gửi lại yêu cầu trong {chooseSignInPadding}s</p>
            </div>
          </form>
        </div>
      </div>
      <div className="login-page">
        <div className="form">
          <Formik
            initialValues={initialValue}
            validationSchema={validationSchema}
            onSubmit={onSubmit}
          >
            <form>
              <div
                style={{
                  display: "flex",
                  flexDirection: "row",
                  paddingLeft: 15,
                  fontFamily: "Roboto",
                }}
              >
                <p style={{ color: "grey", fontSize: headeFontSize }}>
                  Đăng nhập
                </p>
                <Box width={5} />
                <p style={{ fontWeight: "bold", fontSize: headeFontSize }}>
                  Web Admin
                </p>
              </div>
{
  signInForm.map(field=>{
    const controlType=field.elementType
    // const {name,type,placeholder}=field.elementConfig
   return (
    <FormControll
    elementType={controlType}
    {...field}
    />
   )
  })
}
              {/* <TextField
                style={{ width: width, marginBottom: marginBottom }}
                type="text"
                variant="standard"
                label="Tên tài khoản"
                name="username"
              />
              <TextField
                style={{ width: width, marginBottom: marginBottom }}
                variant="standard"
                type="password"
                label="Mật khẩu"
                name="password"
              /> */}
              <div
                className="attensin"
                style={{ textAlign: "right", paddingRight: attensionPadding }}
              >
                <p style={{ fontSize: 12, fontWeight: "bold" }}>
                  Quên mật khẩu?
                </p>
              </div>
              <button type="submit">Đăng nhập</button>
            </form>
          </Formik>
        </div>
      </div>
    </div>
  );
}

export default Signin;
