import { getALL } from "redux/index";
import * as variable from "variables/Variables";
import { SuccessPopUp, FailPopUp } from "components/Modal/Modal";
import React from "react";

// function autoCloseSuccessModal() {
//   setSuccessVisible(true);
//   setTimeout(function () {
//     setSuccessVisible(false);
//   }, 2000);
// }
export const fetchPostList = async (dispatch) => {
  let response;
  // * Bắt try catch ở đây là để tránh lỗi crash ứng dụng lỡ như trong quá trình dispatch đến getAll bị lỗi
  try {
    response = await dispatch(getALL());
    // console.log("result= ", response);

    // * Nếu có sử dụng gì ở local này thì ta cứ lấy response ra mà dùng
  } catch (errpr) {
    console.log("Failed to fetch product list: ", errpr);
  }
  return response;
};

export const handleResult = (result) => {
  const requestStatus = result.meta.requestStatus;
  console.log("status", requestStatus)
  const successVisible = false;
  const failVisible = false;

  switch (requestStatus) {
    case [variable.fulfilled]:
      successVisible = true;
      setTimeout(function () {
        successVisible = false;
      }, 2000);
      return <SuccessPopUp visible={successVisible} length="200px" />;
    case [variable.error]:
      break;
    case [variable.pending]:
      break;
  }
};
