    success: false, // * trường này dùng để hiển thị pop up success nếu như gọi API thành công
    fail: false, //* trường này dùng để hiển thị pop up fail nếu như gọi API thất bại
    error: "",
    signInForm: [
      {
        row: {
          cols: [
            {
              elementType: variable.input,
