import { LoadingPopUp } from "components/Modal/Modal";
import NormalTable from "components/SuperTable/NormalTable";
import React, { useState } from "react";
import { Grid, Col, Row } from "react-bootstrap";
import "react-perfect-scrollbar/dist/css/styles.css";
import { useFormik } from "formik";
import { useDispatch, useSelector } from "react-redux";
import {
  getAllVegetableUnapproved,
  setNotificationCount,
  setVisible,
} from "redux/index";
import { FormControll } from "components/Formik/FormikControl";
import EnhancedTable from "components/SuperTable/NoCustomeTable";
import { prepareVegetableData } from "util/Helper";
import { CardNoFooter, Card } from "../components/Card/Card";
import { MaterialButton } from "../components/CustomButton/MaterialButton";
import {
  getPostTableHeader,
  getVegetableAPIloadingTime,
  getTestTable,
  getShowDetail,
} from "../redux/Selector/Selectors";
import {
  ControlLabel,
  FormControl,
  FormGroup,
  HelpBlock,
} from "react-bootstrap";
import * as variable from "../variables/Variables";

function White() {
  let materialHeader = useSelector((state) => getPostTableHeader(state));
  let testHeader = useSelector((state) => getTestTable(state));
  let loading = useSelector((state) => getVegetableAPIloadingTime(state));
  const [tableBodyData, setTableBodyData] = useState([]);
  const showDetail = useSelector((state) => getShowDetail(state));
  let dispatch = useDispatch();
  const titleStyle = { color: "green", fontWeight: "bold", marginTop: 10 };
  const submitUpdate = () => {};
  const formik = useFormik({
    initialValues: {
      uses: "",
      des: "",
    },
    validationSchema: {},
    onSubmit: submitUpdate,
  });

  //! function sẽ load những rau trồng đc gửi lên để admin duyệt
  function updateVegetableData() {
    let hava = [];
    dispatch(getAllVegetableUnapproved()).then((response) => {
      if (Object.keys(response.payload.data).length !== 0) {
        hava = [...prepareVegetableData(response.payload.data)];
        console.log("table đây ngày29/01= ", hava);
        dispatch(setNotificationCount(hava.length));
        setTableBodyData(hava);
      } else {
        hava = [...prepareVegetableData(response.payload.data)];
        console.log("table đây= ", hava);
        dispatch(setNotificationCount(0));
        setTableBodyData(hava);
      }
    });
  }

  //   React.useEffect(() => {
  // updateVegetableData();
  //   }, []);
  return (
    <React.Fragment>
      <LoadingPopUp visible={loading} length="200px" />
      <div className="content">
        <MaterialButton
          variant="contained"
          color="info"
          size="large"
          style={{ marginRight: 5 }}
          onClick={() => {
            updateVegetableData();
          }}
        >
          Cập nhật
        </MaterialButton>
        <Grid fluid style={{ margin: 0, padding: 0 }}>
          <Row>
            <Col md={6}>
              <CardNoFooter
                titlet="Vegetable list"
                content={
                  <EnhancedTable actionbuttonlist={["read", "remove"]} />
                }
              />
            </Col>
            <Col md={6}>
              <Card
                title="Thông tin chi tiết"
                content={
                  // <div style={{visibility:showDetail}}>
                  <div
                    style={{
                      visibility: "visible",
                      margin: 0,
                      padding: 0,
                      boxSizing: "border-box",
                    }}
                  >
                    <img
                      style={{ border: "1px solid grey", padding: 5 }}
                      width={200}
                      height={200}
                    />
                    <p style={titleStyle}>Tên rau</p>
                    <p>Húng quế</p>
                    <form onSubmit={formik.handleSubmit}>
                      <p style={titleStyle}>Công dụng</p>
                      <FormGroup controlId="formControlUese">
                        <FormControl
                          height={150}
                          name="uses"
                          componentClass="textarea"
                          onChange={formik.handleChange}
                          value={formik.values.uses}
                        />
                      </FormGroup>
                      <p style={titleStyle}>Mô tả</p>
                      <FormGroup controlId="formControlDes">
                        <FormControl
                          height="150px"
                          name={"des"}
                          componentClass="textarea"
                          onChange={formik.handleChange}
                          value={formik.values.des}
                        />
                      </FormGroup>
                    </form>
                  </div>
                }
              />
            </Col>
          </Row>
        </Grid>
      </div>
    </React.Fragment>
  );
}

export default White;
