import React from "react";
import * as variable from "variables/Variables";
import richado from "assets/img/richasdo.jpg";
const vegetableName = {
  color: variable.alternativePrimaryColor,
  margin: 0,
  marginBottom: 5,
};
const normalWord = {
  // color: variable.alternativePrimaryColor,
  margin: 0,
  marginTop: 8,
};
const numberWord = {
  color: variable.materialpinkcolor,
  margin: 0,
  marginTop: 8,
};
function Information(props) {
  const { number, word } = props;
  return (
    <div style={{ display: "flex", flexDirection: "row" }}>
      <p style={numberWord}>{number}</p>
      <p style={normalWord}>{word}</p>
    </div>
  );
}
function Post(props) {
  const { name, image, likeCount, reportCount, caption } = props;
  return (
    <div
      style={{
        background: "lightGrey",
        borderRadius: 5,
        marginBottom: 10,
        padding: 5,
      }}
    >
      {/* <p style={{ color: variable.alternativePrimaryColor }}>{name}</p> */}
      <p style={vegetableName}>Rau cần tây</p>
      <img
        style={{ width: "100%", height: 150, borderRadius: 5 }}
        src={richado}
      />
      {/* <p>{likeCounte} lượt thích</p>
      <p>{reportCount} lượt báo cáo</p> */}
      <Information number="23" word="lượt thích" />
      <p>23 lượt báo cáo</p>
      <p>Ngày hôm nay đẹp quá</p>
    </div>
  );
}

export default Post;
