import React from "react";
import { StyledTableCell } from "./StyledCell";
import IconButton from "@material-ui/core/IconButton";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import * as variable from "variables/Variables";
import { faLock, faLockOpen,faRead } from "@fortawesome/free-solid-svg-icons";
import { faReadme } from '@fortawesome/free-brands-svg-icons'
import { useDispatch, useSelector } from "react-redux";
import { getLocked, removeAdvanceRecordSelected } from "redux/index";
import { Checkbox } from "@material-ui/core";
const ActionButton = (props) => {
  const { row, handlelock, name } = props;
  console.log("row nè", row);
  const dispatch = useDispatch();
  function handleLockAccount(row) {
    let id = row[variable.id];

    // ! Nhấp vào sẽ thành mở/đóng khóa
    dispatch(getLocked(id));
  }
  switch (name) {
    case "lock":
      return (
        <StyledTableCell
          padding="checkbox"
          onClick={() => handleLockAccount(row)}
        >
          <IconButton>
            <FontAwesomeIcon
              style={{
                color: row[variable.islock] == 1 ? "red" : null,
              }}
              icon={row[variable.islock] == 1 ? faLock : faLockOpen}
            />
          </IconButton>
        </StyledTableCell>
      );
    case "edit":
      return null;
    case "read":
      return (
        <StyledTableCell
          padding="checkbox"
          onClick={() => handleLockAccount(row)}
        >
          <IconButton>
            <FontAwesomeIcon
              icon={faReadme}
            />
          </IconButton>
        </StyledTableCell>
      );

    // case "lock":
  }
};

export default ActionButton;
