import React from "react";
import { isTemplateMiddleOrTemplateTail } from "typescript";
import useForceUpdate from "use-force-update";
import "./Wiki.css";
import { wikiVegetableSearch } from "redux/index";
import { useDispatch, useSelector } from "react-redux";
import { getWikiResult,getVegetableAPIloadingTime } from "redux/Selector/Selectors";
import LoadingSpinner,{SwitchSpinner} from "components/LoadingSpinner/LoadingSpinner";

function Wikipedia() {
  const [wikiSearchReturnValues, setWikiSearchReturnValues] = React.useState(
    []
  );
  const [wikiSearchTerms, setWikiSearchTerms] = React.useState("");
  const forceUpdate = useForceUpdate();
  const dispatch = useDispatch();
  const vegetableList = useSelector((state) => getWikiResult(state));
  const loading=useSelector(state=>getVegetableAPIloadingTime(state))

  const useWikiSearchEngine = (e) => {
    e.preventDefault();
    console.log("hiện tại= ", wikiSearchTerms);
    setWikiSearchReturnValues([]);
    const search = async () => {
      await dispatch(wikiVegetableSearch(wikiSearchTerms));
      console.log("leak wiki= ", vegetableList);
      const tmp = [];
      for (let key in vegetableList) {
        tmp.push({
          title: vegetableList[key].title,
          image: vegetableList[key].image,
        });
      }
      setWikiSearchReturnValues(tmp);
      forceUpdate();
