      //       // let page = pointerToThis.state.wikiSearchReturnValues[key2];
      //       setWikiSearchReturnValues(wikiSearchReturnValues[key2])
      //       let page = wikiSearchReturnValues;
      //       let pageID = page.queryResultPageID;
      //       let urlForRetrievingPageURLByPageID = `https://en.wikipedia.org/w/api.php?origin=*&action=query&prop=info&pageids=${pageID}&inprop=url&format=json`;

      //       fetch(urlForRetrievingPageURLByPageID)
      //         .then(function (response) {
      //           return response.json();
      //         })
      //         .then(function (response) {
      //           page.queryResultPageFullURL =
      //             response.query.pages[pageID].fullurl;

      //           //   pointerToThis.forceUpdate();
      //           forceUpdate();
      //         });
      //     }
      //   });
    };
    search();
  };
  const changeWikiSearchTerms = (e) => {
    console.log("value search= ", e.target.value);
    setWikiSearchTerms(e.target.value);
  };

  let wikiSearchResults = [];
  for (var key in wikiSearchReturnValues) {
    wikiSearchResults.push(
      <div className="searchResultDiv" key={key}>
        <h3>{wikiSearchReturnValues[key].title}</h3>
        {/* <p
          className="description"
          dangerouslySetInnerHTML={{
            __html: wikiSearchReturnValues[key3].queryResultPageSnippet,
          }}
        ></p> */}
      </div>
    );
  }
  console.log(wikiSearchResults);

  return (
    <div className="App">
      <h1>Wikipedia Search Engine</h1>
      <form action="">
        <input
          type="text"
          value={wikiSearchTerms || ""}
          onChange={changeWikiSearchTerms}
          placeholder="Search Wikipedia Articles"
        />
        <button type="submit" onClick={useWikiSearchEngine}>
          Search
        </button>
      </form>
      <SwitchSpinner show={loading} size={50}/>
      {setWikiSearchReturnValues.length!=0?wikiSearchResults:<p>Không có kết quả</p>}
    </div>
  );
}

export default Wikipedia;
