import React from "react";
import "../components/SignIn/style.scss";
import TextField from "@material-ui/core/TextField";
import { makeStyles } from "@material-ui/core/styles";

import { Box } from "@material-ui/core";
import { red } from "@material-ui/core/colors";
let width = 240;
let marginBottom = 5;
let headeFontSize = 20;
let choseSignInTypeHeight = 30;
let choseSignInTypeWidth = "80%";

const useStyles = makeStyles({
  button: {
    background: "#e82e1e",
    height: choseSignInTypeHeight,
    padding: 0,
    marginBottom: 10,
    width: choseSignInTypeWidth,
    fontSize: 12,
    // boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
  },
  buttonGoogle: {
    background: "#e82e1e",
    // boxShadow: '0 3px 5px 2px rgba(33, 203, 243, .3)',
  },
  buttonPhone: {
    background: "yellow",
    // boxShadow: '0 3px 5px 2px rgba(33, 203, 243, .3)',
  },
});

// * Style cho may cai nut chon loai dang nhap
let choseStyle = {
  height: choseSignInTypeHeight,
  padding: 0,
  marginBottom: 10,
  width: choseSignInTypeWidth,
  fontSize: 12,
  red: {
    backgroundColor: "red",
  },
  // textTransform:"lowercase"
};
function Signin() {
  const classes = useStyles();

  return (
    <div
      style={{ display: "flex", flexDirection: "row", alignItems: "center" }}
    >
      <div className="login-page">
        <div className="form">
          <button type="submit" style={classes.button}>
            Tên và mật khẩu
          </button>
          <button type="submit" style={choseStyle}>
            Google
          </button>
          <button type="submit" style={choseStyle}>
            Số điện thoại
          </button>
        </div>
      </div>
      <div style={{ fontSize: 25 }}>- -</div>
      <div className="login-page">
        <div className="form">
          <form
            className="login-form"
            // onSubmit={this.handleSubmit}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                paddingLeft: 15,
                fontFamily: "Roboto",
              }}
            >
              <p style={{ color: "grey", fontSize: headeFontSize }}>
                Đăng nhập
              </p>
              <Box width={5} />
              <p style={{ fontWeight: "bold", fontSize: headeFontSize }}>
                Web Admin
              </p>
            </div>

            <TextField
              style={{ width: width, marginBottom: marginBottom }}
              type="text"
              variant="standard"
              label="Tên tài khoản"
              name="username"
              // onChange={(event) => this.handleChange(event)}
            />
            <TextField
              style={{ width: width, marginBottom: marginBottom }}
              variant="standard"
              type="password"
              label="Mật khẩu"
              name="password"
              // onChange={(event) => this.handleChange(event)}
            />
            <div
              style={{
                textAlign: "right",
                color: "grey",
                paddingRight: 10,
                margin: "5px 5px 15px 0",
              }}
            >
              <p style={{ fontSize: 12, fontWeight: "bold" }}>Quên mật khẩu?</p>
            </div>
            <button type="submit">login</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Signin;
