import React from "react";
import { SearchBar } from "./Search";
import SimpleSelect from "../DropDown";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import { useFormik } from "formik";
import { Box } from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { setSearchValue,setAdvanceSearchData } from "../../../redux";

const useStyles = makeStyles((theme) => ({
  around: {
    display: "flex",
    flexDirection: "row",
    width: "max-content",
  },
}));

const searchFilter = [
  {
    key: "age",
    value: "Age",
  },
  {
    key: "name",
    value: "Name",
  },
];

function SearchOption(props) {
  // const dispatch=useDispatch()
  // const formik = useFormik({
  //   initialValues: {
  //     searchvalue: "",
  //     filtervalue: "Name",
  //   },
  //   onSubmit: (values) => {
  //     // alert(JSON.stringify(values, null, 2));
  //     console.log("search value", values);
  //     switch (values["filtervalue"]) {
  //       case "Name":
  //         console.log("result", values["filtervalue"]);
  //         dispatch(setSearchValue(values["searchvalue"]));
  //         break;
  //       case " Age":
  //         console.log("result", values["filtervalue"]);
  //         break;
  //     }
  //   },
  // });
  const formik=props.formikAction
  const classes = useStyles();
  return (
    <form onSubmit={formik.handleSubmit}>
      <div className={classes.around}>
        <SearchBar
          name="searchvalue"
          label="Search"
          searchvalue={formik.values.searchvalue}
          searchOnChange={formik.handleChange}
        />
        <Box width={7} />
        <SimpleSelect
          name="filtervalue"
          label="options"
          options={searchFilter}
          filterValue={formik.values.filtervalue}
          filterOnChange={formik.handleChange}
        />
      </div>
    </form>
  );
}

export default SearchOption;
